import styled from "styled-components";

import { useEffect, useState } from "react";

//방문자 기록 컴포넌트
const BackgroundWrap = styled.div`
  background: linear-gradient(180deg, #ffd25d 0%, #ff984b 100%);
`;
//Background : 모바일로 보이는 영역
const Background = styled.div`
  height: calc(var(--vh, 1vh) * 100);
  max-width: 37.5rem;
  margin: 0px auto;
  display: flex;
  flex-direction: column;
  background: linear-gradient(180deg, #ffd25d 0%, #ff984b 100%);
`;

const HeaderWrap = styled.div`
  height: 10%;
  justify-content: center;
  align-item: center;
  display: flex;
  flex-direction: column;
`;
const FirstHeader = styled.div`
  height: 30%;
  display: flex;

  justify-content: flex-start;
  padding-left: 10px;
`;
const BackIcon = styled.img`
  max-width: 30px;
`;
const TextHeader = styled.div`
  height: 70%;
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: column;
`;
const Text1 = styled.div`
  color: #191919;
  display: flex;
  text-align: center;
  font-family: Pretendard;
  font-size: 25px;
  margin-top: 10px;
  font-style: normal;
  font-weight: 700;
  line-height: 36px;
`;
const Text2 = styled.div`
  color: rgba(25, 25, 25, 0.8);
  text-align: center;
  display: flex;
  font-family: Pretendard;
  font-size: 16px;
  font-style: normal;
  font-weight: 500;
  line-height: 36px;
`;
const HostImg = styled.img`
  max-width: 100px;
  max-height: 150px;
  display: flex;
  position: absolute;
  transform: translate(-40%, -50%);
  top: 40%;
  left: 50%;
`;

const BottomWrap = styled.div`
  height: 60%;
  justify-content: center;
  align-items: center;
  display: flex;
`;

const Bottom = styled.div`
  border-radius: 40px;
  border: 2px solid var(--unnamed, #12151c);
  background: #fff;
  width: 90%;
  height: 90%;
`;
const ButtonWrap = styled.div`
  height: 20%;
  justify-content: center;
  align-items: center;
  display: flex;
`;
const Footer = styled.div`
  border-radius: 40px 40px 0px 0px;
  border: 2px solid var(--unnamed, #12151c);
  background: #fff;
  width: 90%;
  height: 90%;
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: row;
`;
const Icon = styled.img`
  max-height: 50%;
  display: flex;
`;

export function TestBottom() {
  const [imageUrl, setImageUrl] = useState(null);
  const [imagePosition, setImagePosition] = useState({ x: 0, y: 0 });
  const [isImageVisible, setIsImageVisible] = useState(false);
  const [isImageFixed, setIsImageFixed] = useState(false);
  const [hostImageUrl, setHostImageUrl] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3001/imgUrl/1")
      .then((response) => response.json())
      .then((data) => {
        if (data.url) {
          setHostImageUrl(data.url);
        }
      })
      .catch((error) => {
        console.error("오류 발생", error);
      });
  }, []);

  const handleFetchImageAndImageClick = async (event) => {
    try {
      const response = await fetch("http://localhost:3001/imgUrl/2");
      const data = await response.json();
      setImageUrl(data.url);
      setImagePosition({ x: event.clientX, y: event.clientY });
      setIsImageVisible(true);
      setIsImageFixed(false); // 이미지 클릭 시, 스티커 고정 상태를 해제
    } catch (error) {
      console.error("이미지 URL 가져오기 오류:", error);
    }
  };

  const handleCheckButtonClick = () => {
    setIsImageFixed(true); // "Check" 버튼 클릭 시, 스티커 고정
  };

  return (
    <BottomWrap>
      <Bottom>
        {/* 클릭 이벤트 리스너 */}
        {!isImageFixed && (
          <div
            style={{
              display: "flex",
              width: "85%",
              height: "85%",
            }}
            onClick={handleFetchImageAndImageClick}
          ></div>
        )}
        <HostImg src={hostImageUrl} />
        {/* 이미지가 보이는 경우 */}
        {isImageVisible && (
          <img
            src={imageUrl}
            style={{
              maxWidth: "100px",
              position: "absolute",
              top: imagePosition.y,
              left: imagePosition.x,
              zIndex: 9999,
            }}
            alt="Image"
          />
        )}
      </Bottom>
    </BottomWrap>
  );
}

export default TestBottom;
